/**
 * Motus: Pi Day 2022
 * https://owenmcateer.github.io/Motus-Art
 */
const digitsOfPi = '';

// Setup
function setup() {
  createCanvas(540, 540);
  frameRate(30);
}

// Draw tick
function draw() {
  background(39);
  fill(239);

  translate(width / 2, height / 2);

  for (let i = digitsOfPi.length - 1; i > 0; i--) {
    const r = map(i, 0, digitsOfPi.length - frameCount * 2, 400, 0);
    const s = map(i, 0, digitsOfPi.length, 50, 3);
    textSize(s);
    textAlign(CENTER);
    if (r < 0) {
      fill(150);
    }
    else {
      fill(239);
    }

    push();
    rotate(i * 1.2);
    translate(0, r);
    text(digitsOfPi[i], 0, 0);
    pop();
  }
}
