/**
 * Motus: PI Day 2020
 * https://owenmcateer.github.io/Motus-Art
 */
const pi = '';
let showing = 1;
const canvasSize = 1080;
const colour = {};
const previous = {
  x: 884,
  y: 380,
  r: 0,
  showing: 1,
};
const lineLength = 30;
let speed = 0.01;
const speedsSpeed = 0.001;


function setup() {
  createCanvas(canvasSize, canvasSize);
  colorMode(RGB, 255, 255, 255, 1);

  // Colours
  colour.pink = color(209, 49, 222, 0.4);
  colour.green = color(49, 222, 209, 0.4);
  background(39);
  strokeWeight(5);
}


function draw() {
  if (round(showing) !== previous.showing) {
    // Calculate colour
    const phase = map(sin(frameCount / 50), -1, 1, 0, 1);
    stroke(lerpColor(colour.pink, colour.green, phase));

    // Calculate position
    previous.r += (pi[round(showing)] / 10) * PI;
    const x = cos(previous.r) * lineLength + previous.x;
    const y = sin(previous.r) * lineLength + previous.y;

    // Draw line
    line(previous.x, previous.y, x, y);
    previous.x = x;
    previous.y = y;
    previous.showing = round(showing);
  }

  // Speed control
  showing += speed;
  speed += speedsSpeed;
}
